import { Fragment, React, useState } from "react";
import Card from "../UI/Card";
import './AddUser.css';
import Button from "../UI/Button";
import ErrorModal from "../UI/ErrorModal";


const AddUser = (props) => {

  const [username, setUsername] = useState('');
  const [userAge, setUserAge] = useState('');


  const nameChangeHandler = (event) => {
    setUsername(event.target.value);
  }

  const ageChangeHandler = (event) => {
    setUserAge(event.target.value);
  }
  const addUSerHandler = (event) => {
    event.preventDefault();
    if (username.trim().length === 0 || userAge.trim().length === 0) {
      return;
    }
    if (+userAge.length < 1) {
      return;
    }
    console.log(username, userAge);
    props.onAddUser(username, userAge);
    setUsername('');
    setUserAge('');
  }

  const submitHandler = (event) => {
    console.log(event);
  }
  return (
    <Fragment>
      <ErrorModal title="An error occured" message="soemthing went wrong" />
      <Card className="input">
        <form onSubmit={addUSerHandler}>
          <label htmlFor="username">UserName</label>
          <input id="username" type="text" value={username} onChange={nameChangeHandler} />
          <label htmlFor="age">Age(Years)</label>
          <input id="age" type="number" value={userAge} onChange={ageChangeHandler} />
          <Button type="submit" onClick={submitHandler}>Add User</Button>
        </form>
      </Card>
    </Fragment>


  )

}
export default AddUser;
